import React from 'react';

const Pokemon = (props) => {
    const { pokemon } = props;
    return (
        <div className='pokemon-card'>
            <div className='pokemon-img'>
            <img src={pokemon.sprites.front.default} 
            alt={pokemon.name}/>
            </div>
            <div>
            <div>
                <h3>{pokemon.name}</h3>
                <div>#{pokemon.id}</div>
            </div>
            <div>
                {pokemon.types.map((type, idx) => {
                    return <div key={idx}>{type.name} </div>;
                })}
            </div>
            <div>❤️</div>
            </div>
        </div>
    )
};

export default Pokemon;
